# sneaker_manager/app/database.pyfrom sqlalchemy import create_enginefrom sqlalchemy.orm import sessionmaker, scoped_sessionfrom contextlib import contextmanager  # 新增导入from .models import BaseDATABASE_URL = "sqlite:///sneakers.db"engine = create_engine(DATABASE_URL)SessionLocal = scoped_session(sessionmaker(    autocommit=False,    autoflush=False,    bind=engine))def init_db():    Base.metadata.create_all(bind=engine)@contextmanager  # 添加装饰器def get_db():    db = SessionLocal()    try:        yield db    finally:        db.close()